import{_ as d,c as l,o as b,a as i}from"./app.7f721c1a.js";const c='{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"guide/Vim\u5907\u5FD8\u5F55\u6E05\u5355.md","lastUpdated":1654579673000}',k={},t=i('<h1>Vim Cheat Sheet</h1><p><img src="https://tva1.sinaimg.cn/large/e6c9d24egy1h2v6r4hrerj20u00u2dhw.jpg" alt="\u7CBE\u901A VIM \uFF0C\u6B64\u6587\u5C31\u591F\u4E86" style="zoom:33%;"></p><main><div class="container"><div class="commands-container"><div class="grid-block"><div class="grid-lg-1-3"><h2>\u5168\u5C40</h2><ul><li><kbd>:h[elp] \u5173\u952E\u5B57</kbd> - \u6253\u5F00\u5173\u952E\u5B57\u5E2E\u52A9 </li><li><kbd>:sav[eas] \u6587\u4EF6\u540D</kbd> - \u53E6\u5B58\u4E3A </li><li><kbd>:clo[se]</kbd> - \u5173\u95ED\u5F53\u524D\u7A97\u53E3 </li><li><kbd>:ter[minal]</kbd> - \u6253\u5F00\u4E00\u4E2Ashell\u7A97\u53E3 </li><li><kbd>K</kbd> - \u6253\u5F00\u5149\u6807\u6240\u5728\u5355\u8BCD\u7684man\u9875\u9762 </li></ul><div class="well"><strong>Tip</strong> Run <kbd>vimtutor</kbd> in a terminal to learn the first Vim commands. </div><h2>\u79FB\u52A8\u5149\u6807</h2><ul><li><kbd>h</kbd> - \u5DE6\u79FB\u5149\u6807 </li><li><kbd>j</kbd> - \u4E0B\u79FB\u5149\u6807 </li><li><kbd>k</kbd> - \u4E0A\u79FB\u5149\u6807 </li><li><kbd>l</kbd> - \u53F3\u79FB\u5149\u6807 </li><li><kbd>gj</kbd> - move cursor down (multi-line text) </li><li><kbd>gk</kbd> - move cursor up (multi-line text) </li><li><kbd>H</kbd> - \u79FB\u52A8\u5230\u5F53\u524D\u9875\u9762\u9876\u90E8 </li><li><kbd>M</kbd> - \u79FB\u52A8\u5230\u5F53\u524D\u9875\u9762\u4E2D\u95F4 </li><li><kbd>L</kbd> - \u79FB\u52A8\u5230\u5F53\u524D\u9875\u9762\u5E95\u90E8 </li><li><kbd>w</kbd> - \u79FB\u52A8\u5230\u4E0B\u4E2A\u5355\u8BCD\u5F00\u5934 </li><li><kbd>W</kbd> - \u79FB\u52A8\u5230\u4E0B\u4E2A\u5355\u8BCD\u5F00\u5934(\u5355\u8BCD\u542B\u6807\u70B9) </li><li><kbd>e</kbd> - \u79FB\u52A8\u5230\u4E0B\u4E2A\u5355\u8BCD\u7ED3\u5C3E </li><li><kbd>E</kbd> - \u79FB\u52A8\u5230\u4E0B\u4E2A\u5355\u8BCD\u7ED3\u5C3E(\u5355\u8BCD\u542B\u6807\u70B9) </li><li><kbd>b</kbd> - \u79FB\u52A8\u5230\u4E0A\u4E2A\u5355\u8BCD\u5F00\u5934 </li><li><kbd>B</kbd> - \u79FB\u52A8\u5230\u4E0A\u4E2A\u5355\u8BCD\u5F00\u5934(\u5355\u8BCD\u542B\u6807\u70B9) </li><li><kbd>ge</kbd> - \u79FB\u52A8\u5230\u4E0A\u4E2A\u5355\u8BCD\u7ED3\u5C3E </li><li><kbd>gE</kbd> - \u79FB\u52A8\u5230\u4E0A\u4E2A\u5355\u8BCD\u7ED3\u5C3E(\u5355\u8BCD\u542B\u6807\u70B9) </li><li><kbd>%</kbd> - \u8DF3\u8F6C\u5230\u914D\u5BF9\u7684\u7B26\u53F7(\u9ED8\u8BA4\u652F\u6301\u7684\u914D\u5BF9\u7B26\u53F7\u7EC4:: &#39;()&#39;, &#39;{}&#39;, &#39;[]&#39; - \u5728vim\u4E2D\u4F7F\u7528 <code>:h matchpairs</code> \u83B7\u5F97\u66F4\u591A\u4FE1\u606F) </li><li><kbd>0</kbd> - \u79FB\u52A8\u5230\u884C\u9996 </li><li><kbd>^</kbd> - \u79FB\u52A8\u5230\u884C\u9996\u7684\u975E\u7A7A\u767D\u7B26 </li><li><kbd>$</kbd> - \u79FB\u52A8\u5230\u884C\u5C3E </li><li><kbd>g_</kbd> - \u79FB\u52A8\u5230\u884C\u5185\u6700\u540E\u4E00\u4E2A\u975E\u7A7A\u767D\u7B26 </li><li><kbd>gg</kbd> - \u79FB\u52A8\u5230\u6587\u4EF6\u7B2C\u4E00\u884C </li><li><kbd>G</kbd> - \u79FB\u52A8\u5230\u6587\u4EF6\u6700\u540E\u4E00\u884C </li><li><kbd>5gg</kbd> or <kbd>5G</kbd> - \u79FB\u52A8\u5230\u7B2C\u4E94\u884C </li><li><kbd>gd</kbd> - \u8DF3\u8F6C\u5230\u5C40\u90E8\u5B9A\u4E49 </li><li><kbd>gD</kbd> - \u8DF3\u8F6C\u5230\u5168\u5C40\u5B9A\u4E49 </li><li><kbd>fx</kbd> - \u79FB\u52A8\u5230\u5B57\u7B26 x \u4E0B\u6B21\u51FA\u73B0\u7684\u4F4D\u7F6E </li><li><kbd>tx</kbd> - \u79FB\u52A8\u5230\u5B57\u7B26 x \u4E0B\u6B21\u51FA\u73B0\u7684\u4F4D\u7F6E\u7684\u524D\u4E00\u4E2A\u5B57\u7B26 </li><li><kbd>Fx</kbd> - \u79FB\u52A8\u5230\u5B57\u7B26 x \u4E0A\u6B21\u51FA\u73B0\u7684\u4F4D\u7F6E </li><li><kbd>Tx</kbd> - \u79FB\u52A8\u5230\u5B57\u7B26 x \u4E0A\u6B21\u51FA\u73B0\u7684\u4F4D\u7F6E\u7684\u540E\u4E00\u4E2A\u5B57\u7B26 </li><li><kbd>;</kbd> - \u91CD\u590D\u4E4B\u524D\u7684f\u3001t\u3001F\u3001T\u64CD\u4F5C </li><li><kbd>,</kbd> - \u53CD\u5411\u91CD\u590D\u4E4B\u524D\u7684f\u3001t\u3001F\u3001T\u64CD\u4F5C </li><li><kbd>}</kbd> - \u79FB\u52A8\u5230\u4E0B\u4E00\u4E2A\u6BB5\u843D (\u5F53\u7F16\u8F91\u4EE3\u7801\u65F6\u5219\u4E3A\u51FD\u6570\uFF0F\u4EE3\u7801\u5757) </li><li><kbd>{</kbd> - \u79FB\u52A8\u5230\u4E0A\u4E00\u4E2A\u6BB5\u843D (\u5F53\u7F16\u8F91\u4EE3\u7801\u65F6\u5219\u4E3A\u51FD\u6570\uFF0F\u4EE3\u7801\u5757) </li><li><kbd>zz</kbd> - \u79FB\u52A8\u5C4F\u5E55\u4F7F\u5149\u6807\u5C45\u4E2D </li><li><kbd>Ctrl</kbd> + <kbd>e</kbd> - \u5411\u4E0B\u79FB\u52A8\u5C4F\u5E55\u4E00\u884C(\u4FDD\u6301\u5149\u6807\u4E0D\u52A8) </li><li><kbd>Ctrl</kbd> + <kbd>y</kbd> - \u5411\u4E0A\u79FB\u52A8\u5C4F\u5E55\u4E00\u884C(\u4FDD\u6301\u5149\u6807\u4E0D\u52A8) </li><li><kbd>Ctrl</kbd> + <kbd>b</kbd> - \u5411\u4E0A\u6EDA\u52A8\u4E00\u5C4F </li><li><kbd>Ctrl</kbd> + <kbd>f</kbd> - \u5411\u4E0B\u6EDA\u52A8\u4E00\u5C4F </li><li><kbd>Ctrl</kbd> + <kbd>d</kbd> - \u5411\u4E0B\u6EDA\u52A8\u534A\u5C4F </li><li><kbd>Ctrl</kbd> + <kbd>u</kbd> - \u5411\u4E0A\u6EDA\u52A8\u534A\u5C4F </li></ul><div class="well"><strong>Tip</strong> \u547D\u4EE4\u524D\u8FFD\u52A0\u6570\u5B57\u8868\u793A\u547D\u4EE4\u7684\u91CD\u590D\u6B21\u6570, \u6BD4\u5982 <kbd>4j</kbd> \u8868\u793A\u5411\u4E0B\u79FB\u52A8\u56DB\u884C </div><h2>\u63D2\u5165\u6A21\u5F0F - \u63D2\u5165/\u8FFD\u52A0\u6587\u672C</h2><ul><li><kbd>i</kbd> - \u4ECE\u5149\u6807\u524D\u5F00\u59CB\u63D2\u5165\u5B57\u7B26 </li><li><kbd>I</kbd> - \u4ECE\u884C\u9996\u5F00\u59CB\u63D2\u5165\u5B57\u7B26 </li><li><kbd>a</kbd> - \u4ECE\u5149\u6807\u540E\u5F00\u59CB\u63D2\u5165\u5B57\u7B26 </li><li><kbd>A</kbd> - \u4ECE\u884C\u5C3E\u5F00\u59CB\u63D2\u5165\u5B57\u7B26 </li><li><kbd>o</kbd> - \u5728\u5F53\u524D\u884C\u4E4B\u4E0B\u53E6\u8D77\u4E00\u884C, \u5F00\u59CB\u63D2\u5165\u5B57\u7B26 </li><li><kbd>O</kbd> - \u5728\u5F53\u524D\u884C\u4E4B\u4E0A\u53E6\u8D77\u4E00\u884C, \u5F00\u59CB\u63D2\u5165\u5B57\u7B26 </li><li><kbd>ea</kbd> - \u4ECE\u5F53\u524D\u5355\u8BCD\u672B\u5C3E\u5F00\u59CB\u63D2\u5165 </li><li><kbd>Ctrl</kbd> + <kbd>h</kbd> - \u5728\u63D2\u5165\u6A21\u5F0F\u4E0B\uFF0C\u5220\u9664\u5149\u6807\u524D\u7684\u5B57\u7B26 </li><li><kbd>Ctrl</kbd> + <kbd>w</kbd> - \u5728\u63D2\u5165\u6A21\u5F0F\u4E0B\uFF0C\u5220\u9664\u5149\u6807\u524D\u7684\u5355\u8BCD </li><li><kbd>Ctrl</kbd> + <kbd>j</kbd> - \u5728\u63D2\u5165\u6A21\u5F0F\u4E0B\uFF0C\u53E6\u8D77\u4E00\u884C </li><li><kbd>Ctrl</kbd> + <kbd>t</kbd> - \u5728\u63D2\u5165\u6A21\u5F0F\u4E0B\uFF0C\u5411\u53F3\u7F29\u8FDB\uFF0C\u5BBD\u5EA6\u7531 shiftwidth \u63A7\u5236 </li><li><kbd>Ctrl</kbd> + <kbd>d</kbd> - \u5728\u63D2\u5165\u6A21\u5F0F\u4E0B\uFF0C\u5411\u5DE6\u7F29\u8FDB\uFF0C\u5BBD\u5EA6\u7531 shiftwidth \u63A7\u5236 </li><li><kbd>Ctrl</kbd> + <kbd>n</kbd> - \u5728\u63D2\u5165\u6A21\u5F0F\u4E0B\uFF0C\u5728\u5149\u6807\u4E4B\u524D\u63D2\u5165\u81EA\u52A8\u8865\u5168\u7684\u4E0B\u4E00\u4E2A\u5339\u914D\u9879 </li><li><kbd>Ctrl</kbd> + <kbd>p</kbd> - \u5728\u63D2\u5165\u6A21\u5F0F\u4E0B\uFF0C\u5728\u5149\u6807\u4E4B\u524D\u63D2\u5165\u81EA\u52A8\u8865\u5168\u7684\u4E0A\u4E00\u4E2A\u5339\u914D\u9879 </li><li><kbd>Ctrl</kbd> + <kbd>rx</kbd> - \u63D2\u5165\u5BC4\u5B58\u5668 x \u7684\u5185\u5BB9 </li><li><kbd>Ctrl</kbd> + <kbd>ox</kbd> - \u6682\u65F6\u8FDB\u5165\u6B63\u5E38\u6A21\u5F0F\u4EE5\u53D1\u51FA\u4E00\u4E2A\u6B63\u5E38\u6A21\u5F0F\u547D\u4EE4 x\u3002 </li><li><kbd>Esc</kbd> - \u9000\u51FA\u63D2\u5165\u6A21\u5F0F </li></ul></div><div class="grid-lg-1-3"><h2>\u7F16\u8F91\u6587\u672C</h2><ul><li><kbd>r</kbd> - \u66FF\u6362\u5F53\u524D\u5B57\u7B26 </li><li><kbd>R</kbd> - \u5728 <kbd>ESC</kbd> \u6309\u4E0B\u4E4B\u524D\uFF0C\u66FF\u6362\u591A\u4E2A\u5B57\u7B26 </li><li><kbd>J</kbd> - \u5C06\u4E0B\u4E00\u884C\u5408\u5E76\u5230\u5F53\u524D\u884C, \u5E76\u5728\u4E24\u90E8\u5206\u6587\u672C\u4E4B\u95F4\u63D2\u5165\u4E00\u4E2A\u7A7A\u683C </li><li><kbd>gJ</kbd> - \u5C06\u4E0B\u4E00\u884C\u5408\u5E76\u5230\u5F53\u524D\u884C, \u4E24\u90E8\u5206\u6587\u672C\u4E4B\u95F4\u4E0D\u542B\u7A7A\u683C </li><li><kbd>gwip</kbd> - \u91CD\u65B0\u8C03\u6574\u6BB5\u843D </li><li><kbd>g~</kbd> - switch case up to motion </li><li><kbd>gu</kbd> - \u5C0F\u5199\u64CD\u4F5C\u4FEE\u9970\u7B26 </li><li><kbd>gU</kbd> - \u5927\u5199\u64CD\u4F5C\u4FEE\u9970\u7B26 </li><li><kbd>cc</kbd> - \u5C06\u5149\u6807\u6240\u5728\u7684\u884C\u5220\u9664, \u7136\u540E\u8FDB\u5165\u63D2\u5165\u6A21\u5F0F </li><li><kbd>c$</kbd> or <kbd>C</kbd> - \u5C06\u5149\u6807\u5904\u5230\u884C\u5C3E\u5220\u9664, \u7136\u540E\u8FDB\u5165\u63D2\u5165\u6A21\u5F0F </li><li><kbd>ciw</kbd> - \u5C06\u5149\u6807\u6240\u5728\u7684\u5355\u8BCD\u5220\u9664, \u7136\u540E\u8FDB\u5165\u63D2\u5165\u6A21\u5F0F </li><li><kbd>cw</kbd> or <kbd>ce</kbd> - \u4ECE\u5149\u6807\u4F4D\u7F6E\u5F00\u59CB, \u4FEE\u6539\u5355\u8BCD </li><li><kbd>s</kbd> - \u5220\u9664\u5F53\u524D\u5B57\u7B26, \u7136\u540E\u8FDB\u5165\u63D2\u5165\u6A21\u5F0F </li><li><kbd>S</kbd> - \u6E05\u7A7A\u5F53\u524D\u884C, \u7136\u540E\u8FDB\u5165\u63D2\u5165\u6A21\u5F0F (\u540C<kbd>cc</kbd>) </li><li><kbd>xp</kbd> - \u5F53\u524D\u5B57\u7B26\u540E\u79FB </li><li><kbd>u</kbd> - \u64A4\u9500 </li><li><kbd>U</kbd> - \u64A4\u9500\u4E0A\u4E00\u6B21\u7684\u6539\u52A8\u884C\u7684\u64CD\u4F5C </li><li><kbd>Ctrl</kbd> + <kbd>r</kbd> - \u91CD\u505A\uFF08\u53D6\u6D88\u64A4\u9500\uFF09 </li><li><kbd>.</kbd> - \u518D\u6B21\u6267\u884C\u4E0A\u4E2A\u547D\u4EE4 </li></ul><h2>\u9009\u62E9\u6587\u672C\uFF08\u53EF\u89C6\u5316\u6A21\u5F0F\uFF09</h2><ul><li><kbd>v</kbd> - \u8FDB\u5165\u53EF\u89C6\u5316\u6A21\u5F0F, \u79FB\u52A8\u5149\u6807\u9AD8\u4EAE\u9009\u62E9, \u7136\u540E\u53EF\u4EE5\u5BF9\u9009\u62E9\u7684\u6587\u672C\u6267\u884C\u547D\u4EE4(\u6BD4\u5982&lt;kbd&gt;y&lt;/kbd&gt;-\u590D\u5236) </li><li><kbd>V</kbd> - \u8FDB\u5165\u53EF\u89C6\u5316\u6A21\u5F0F(\u884C\u7C92\u5EA6\u9009\u62E9) </li><li><kbd>o</kbd> - \u5207\u6362\u5149\u6807\u5230\u9009\u62E9\u533A\u5F00\u5934/\u7ED3\u5C3E </li><li><kbd>Ctrl</kbd> + <kbd>v</kbd> - \u8FDB\u5165\u53EF\u89C6\u5316\u6A21\u5F0F(\u77E9\u9635\u9009\u62E9) </li><li><kbd>O</kbd> - \u5207\u6362\u5149\u6807\u5230\u9009\u62E9\u533A\u7684\u89D2 </li><li><kbd>aw</kbd> - \u9009\u62E9\u5F53\u524D\u5355\u8BCD </li><li><kbd>ab</kbd> - \u9009\u62E9\u88AB () \u5305\u88F9\u7684\u533A\u57DF(\u542B\u62EC\u53F7) </li><li><kbd>aB</kbd> - \u9009\u62E9\u88AB {} \u5305\u88F9\u7684\u533A\u57DF(\u542B\u82B1\u62EC\u53F7) </li><li><kbd>at</kbd> - \u9009\u62E9\u88AB &lt;&gt; \u6807\u7B7E\u5305\u88F9\u7684\u533A\u57DF(\u542B&lt;&gt;\u6807\u7B7E) </li><li><kbd>ib</kbd> - \u9009\u62E9\u88AB () \u5305\u88F9\u7684\u533A\u57DF(\u4E0D\u542B\u62EC\u53F7) </li><li><kbd>iB</kbd> - \u9009\u62E9\u88AB {} \u5305\u88F9\u7684\u533A\u57DF(\u4E0D\u542B\u82B1\u62EC\u53F7) </li><li><kbd>it</kbd> - \u9009\u62E9\u88AB &lt;&gt; \u6807\u7B7E\u5305\u88F9\u7684\u533A\u57DF(\u4E0D\u542B&lt;&gt;\u6807\u7B7E) </li><li><kbd>Esc</kbd> - \u9000\u51FA\u53EF\u89C6\u5316\u6A21\u5F0F </li></ul><div class="well"><strong>Tip</strong> \u4E5F\u53EF\u4EE5\u4F7F\u7528 <kbd>(</kbd> \u548C <kbd>{</kbd> \u5206\u522B\u4EE3\u66FF <kbd>b</kbd> \u548C <kbd>B</kbd></div><h2>\u53EF\u89C6\u5316\u6A21\u5F0F\u547D\u4EE4</h2><ul><li><kbd>&gt;</kbd> - \u5411\u53F3\u7F29\u8FDB </li><li><kbd>&lt;</kbd> - \u5411\u5DE6\u7F29\u8FDB </li><li><kbd>y</kbd> - \u590D\u5236 </li><li><kbd>d</kbd> - \u526A\u5207 </li><li><kbd>~</kbd> - \u5927\u5C0F\u5199\u5207\u6362 </li><li><kbd>u</kbd> - \u5C06\u9009\u4E2D\u6587\u672C\u8F6C\u6362\u4E3A\u5C0F\u5199 </li><li><kbd>U</kbd> - \u5C06\u9009\u4E2D\u6587\u672C\u8F6C\u6362\u4E3A\u5927\u5199 </li></ul><h2>\u5BC4\u5B58\u5668</h2><ul><li><kbd>:reg[isters]</kbd> - \u663E\u793A\u5BC4\u5B58\u5668\u5185\u5BB9 </li><li><kbd>&quot;xy</kbd> - \u590D\u5236\u5185\u5BB9\u5230\u5BC4\u5B58\u5668 x </li><li><kbd>&quot;xp</kbd> - \u7C98\u8D34\u5BC4\u5B58\u5668 x \u4E2D\u7684\u5185\u5BB9 </li><li><kbd>&quot;+y</kbd> - \u590D\u5236\u5185\u5BB9\u5230\u7CFB\u7EDF\u526A\u8D34\u677F\u5BC4\u5B58\u5668 </li><li><kbd>&quot;+p</kbd> - \u7C98\u8D34\u7CFB\u7EDF\u526A\u8D34\u677F\u5BC4\u5B58\u5668\u7684\u5185\u5BB9 </li></ul><div class="well"><strong>Tip</strong> \u5BC4\u5B58\u5668\u88AB\u5B58\u50A8\u5728 ~/.viminfo \u4E2D, \u5728\u4E0B\u6B21\u91CD\u542Fvim\u65F6\u4ECD\u4F1A\u52A0\u8F7D </div><div class="well"><strong>Tip</strong> \u7279\u6B8A\u5BC4\u5B58\u5668\uFF1A <p> \u2003<kbd>0</kbd> - \u4E0A\u6B21\u590D\u5236<br> \u2003<kbd>&quot;</kbd> - \u672A\u547D\u540D\u5BC4\u5B58\u5668\uFF0C\u4E0A\u6B21\u590D\u5236\u6216\u5220\u9664<br> \u2003<kbd>%</kbd> - \u5F53\u524D\u6587\u4EF6\u540D<br> \u2003<kbd>#</kbd> - \u8F6E\u6362\u6587\u4EF6\u540D<br> \u2003<kbd>*</kbd> - \u526A\u8D34\u677F\u5185\u5BB9 (X11 primary)<br> \u2003<kbd>+</kbd> - \u526A\u8D34\u677F\u5185\u5BB9 (X11 clipboard)<br> \u2003<kbd>/</kbd> - \u4E0A\u6B21\u641C\u7D22\u7684pattern<br> \u2003<kbd>:</kbd> - \u4E0A\u6B21\u6267\u884C\u7684\u547D\u4EE4<br> \u2003<kbd>.</kbd> - \u4E0A\u6B21\u63D2\u5165\u7684\u6587\u672C<br> \u2003<kbd>-</kbd> - \u4E0A\u6B21\u526A\u5207\u7684\u77ED\u4E8E\u4E00\u884C\u7684\u6587\u672C<br> \u2003<kbd>=</kbd> - \u8868\u8FBE\u5F0F\u5BC4\u5B58\u5668<br> \u2003<kbd>_</kbd> - \u9ED1\u6D1E\u5BC4\u5B58\u5668<br></p></div><h2>\u6807\u8BB0</h2><ul><li><kbd>:marks</kbd> - \u6807\u8BB0\u5217\u8868 </li><li><kbd>ma</kbd> - \u8BBE\u7F6E\u5F53\u524D\u4F4D\u7F6E\u4E3A\u6807\u8BB0 a </li><li><kbd>`a</kbd> - \u8DF3\u8F6C\u5230\u6807\u8BB0 a \u7684\u4F4D\u7F6E </li><li><kbd>y`a</kbd> - \u590D\u5236\u5F53\u524D\u4F4D\u7F6E\u5230\u6807\u8BB0 a \u7684\u5185\u5BB9 </li><li><kbd>`0</kbd> - go to the position where Vim was previously exited </li><li><kbd>`&quot;</kbd> - go to the position when last editing this file </li><li><kbd>`.</kbd> - go to the position of the last change in this file </li><li><kbd>``</kbd> - go to the position before the last jump </li><li><kbd>:ju[mps]</kbd> - list of jumps </li><li><kbd>Ctrl</kbd> + <kbd>i</kbd> - go to newer position in jump list </li><li><kbd>Ctrl</kbd> + <kbd>o</kbd> - go to older position in jump list </li><li><kbd>:changes</kbd> - list of changes </li><li><kbd>g,</kbd> - go to newer position in change list </li><li><kbd>g;</kbd> - go to older position in change list </li><li><kbd>Ctrl</kbd> + <kbd>]</kbd> - jump to the tag under cursor </li></ul><div class="well"><strong>Tip</strong> To jump to a mark you can either use a backtick (<kbd>`</kbd>) or an apostrophe (<kbd>&#39;</kbd>). Using an apostrophe jumps to the beginning (first non-blank) of the line holding the mark. </div></div><div class="grid-lg-1-3"><h2>\u5B8F</h2><ul><li><kbd>qa</kbd> - \u5F55\u5236\u5B8F a </li><li><kbd>q</kbd> - \u505C\u6B62\u5F55\u5236\u5B8F </li><li><kbd>@a</kbd> - \u6267\u884C\u5B8F a </li><li><kbd>@@</kbd> - \u91CD\u65B0\u6267\u884C\u4E0A\u6B21\u6267\u884C\u7684\u5B8F </li></ul><h2>\u526A\u5207, \u590D\u5236, \u7C98\u8D34</h2><ul><li><kbd>yy</kbd> - \u590D\u5236\u5F53\u524D\u884C </li><li><kbd>2yy</kbd> - \u590D\u5236 2 \u884C </li><li><kbd>yw</kbd> - \u590D\u5236\u5F53\u524D\u5355\u8BCD </li><li><kbd>yiw</kbd> - \u590D\u5236\u5149\u6807\u5904\u7684\u5355\u8BCD </li><li><kbd>yaw</kbd> - \u590D\u5236\u5149\u6807\u5904\u7684\u5355\u8BCD\u53CA\u5176\u524D\u540E\u7684\u7A7A\u683C </li><li><kbd>y$</kbd> or <kbd>Y</kbd> - \u590D\u5236, \u4ECE\u5149\u6807\u4F4D\u7F6E\u5230\u884C\u672B </li><li><kbd>p</kbd> - \u5728\u5149\u6807\u540E\u7C98\u8D34 </li><li><kbd>P</kbd> - \u5728\u5149\u6807\u524D\u7C98\u8D34 </li><li><kbd>gp</kbd> - put (paste) the clipboard after cursor and leave cursor after the new text </li><li><kbd>gP</kbd> - put (paste) before cursor and leave cursor after the new text </li><li><kbd>dd</kbd> - \u526A\u5207\u5F53\u524D\u884C </li><li><kbd>2dd</kbd> - \u526A\u5207 2 \u884C </li><li><kbd>dw</kbd> - \u526A\u5207\u5F53\u524D\u5355\u8BCD </li><li><kbd>diw</kbd> - \u5220\u9664\u5149\u6807\u5904\u7684\u5355\u8BCD </li><li><kbd>daw</kbd> - \u5220\u9664\u5149\u6807\u5904\u7684\u5355\u8BCD\u53CA\u5176\u524D\u540E\u7684\u7A7A\u683C </li><li><kbd>d$</kbd> or <kbd>D</kbd> - \u526A\u5207, \u4ECE\u5149\u6807\u4F4D\u7F6E\u5230\u884C\u672B (\u540C&lt;kbd&gt;D&lt;/kbd&gt;) </li><li><kbd>x</kbd> - \u526A\u5207\u5F53\u524D\u5B57\u7B26 </li></ul><h2>\u6587\u5B57\u7F29\u8FDB</h2><ul><li><kbd>&gt;&gt;</kbd> - \u5C06\u5F53\u524D\u884C\u5411\u53F3\u7F29\u8FDB\uFF0C\u5BBD\u5EA6\u7531 shiftwidth \u63A7\u5236 </li><li><kbd>&lt;&lt;</kbd> - \u5C06\u5F53\u524D\u884C\u5411\u5DE6\u7F29\u8FDB\uFF0C\u5BBD\u5EA6\u7531 shiftwidth \u63A7\u5236 </li><li><kbd>&gt;%</kbd> - \u5411\u53F3\u7F29\u8FDB () \u6216 {} \u5185\u7684\u533A\u57DF (\u5149\u6807\u9700\u7F6E\u4E8E\u62EC\u53F7\u4E0A) </li><li><kbd>&gt;ib</kbd> - \u5411\u53F3\u7F29\u8FDB () \u5185\u7684\u533A\u57DF </li><li><kbd>&gt;at</kbd> - \u5411\u53F3\u7F29\u8FDB &lt;&gt; \u6807\u7B7E\u5185\u7684\u533A\u57DF </li><li><kbd>3==</kbd> - \u81EA\u52A8\u7F29\u8FDB 3 \u884C </li><li><kbd>=%</kbd> - \u81EA\u52A8\u7F29\u8FDB () \u6216 {} \u5185\u7684\u533A\u57DF (\u5149\u6807\u9700\u7F6E\u4E8E\u62EC\u53F7\u4E0A) </li><li><kbd>=iB</kbd> - \u81EA\u52A8\u7F29\u8FDB {} \u5185\u7684\u533A\u57DF (\u5149\u6807\u9700\u7F6E\u4E8E\u62EC\u53F7\u4E0A) </li><li><kbd>gg=G</kbd> - \u81EA\u52A8\u7F29\u8FDB\u6574\u4E2A\u7F13\u51B2\u533A </li><li><kbd>]p</kbd> - \u7C98\u8D34\u5E76\u8C03\u6574\u7F29\u8FDB\u81F3\u5F53\u524D\u884C </li></ul><h2>\u9000\u51FA</h2><ul><li><kbd>:w</kbd> - \u4FDD\u5B58 </li><li><kbd>:w !sudo tee %</kbd> - \u4F7F\u7528 sudo \u4FDD\u5B58\u5F53\u524D\u6587\u4EF6 </li><li><kbd>:wq</kbd> or <kbd>:x</kbd> or <kbd>ZZ</kbd> - \u4FDD\u5B58\u5E76\u9000\u51FA </li><li><kbd>:q</kbd> - \u9000\u51FA(\u4FEE\u6539\u672A\u4FDD\u5B58\u65F6\u8B66\u544A) </li><li><kbd>:q!</kbd> or <kbd>ZQ</kbd> - \u4E0D\u4FDD\u5B58\u5F3A\u5236\u9000\u51FA </li><li><kbd>:wqa</kbd> - \u4FDD\u5B58\u6240\u6709\u6807\u7B7E\u9875\u5E76\u5168\u90E8\u9000\u51FA </li></ul><h2>\u67E5\u627E/\u66FF\u6362</h2><ul><li><kbd>/pattern</kbd> - \u67E5\u627E&lt;kbd&gt;pattern&lt;/kbd&gt; </li><li><kbd>?pattern</kbd> - \u5411\u4E0A\u67E5\u627E&lt;kbd&gt;pattern&lt;/kbd&gt; </li><li><kbd>\\vpattern</kbd> - &lt;kbd&gt;pattern&lt;/kbd&gt; \u4E2D\u7684\u975E\u5B57\u6BCD\u6570\u5B57\u5B57\u7B26\u88AB\u89C6\u4E3A\u6B63\u5219\u8868\u8FBE\u5F0F\u7279\u6B8A\u5B57\u7B26 (\u4E0D\u9700\u8F6C\u4E49\u5B57\u7B26) </li><li><kbd>n</kbd> - \u67E5\u627E\u4E0B\u4E00\u4E2A </li><li><kbd>N</kbd> - \u67E5\u627E\u4E0A\u4E00\u4E2A </li><li><kbd>:%s/old/new/g</kbd> - \u66FF\u6362\u5168\u90E8 </li><li><kbd>:%s/old/new/gc</kbd> - (\u9010\u4E2A)\u66FF\u6362 </li><li><kbd>:noh[lsearch]</kbd> - \u79FB\u9664\u641C\u7D22\u7ED3\u679C\u7684\u9AD8\u4EAE\u663E\u793A </li></ul><h2>\u591A\u6587\u4EF6\u641C\u7D22</h2><ul><li><kbd>:vim[grep] /pattern/ {`{file}`}</kbd> - \u5728\u591A\u4E2A\u6587\u4EF6\u4E2D\u641C\u7D22 &lt;kbd&gt;pattern&lt;/kbd&gt; </li></ul><div class="well"> e.g. <kbd>:vim[grep] /foo/ **/*</kbd></div><ul><li><kbd>:cn[ext]</kbd> - \u79FB\u52A8\u81F3\u4E0B\u4E00\u4E2A </li><li><kbd>:cp[revious]</kbd> - \u79FB\u52A8\u81F3\u4E0A\u4E00\u4E2A </li><li><kbd>:cope[n]</kbd> - \u6253\u5F00\u641C\u7D22\u7ED3\u679C\u5217\u8868 </li><li><kbd>:ccl[ose]</kbd> - close the quickfix window </li></ul><h2>\u6807\u7B7E</h2><ul><li><kbd>:tabnew</kbd> or <kbd>:tabnew {page.words.file}</kbd> - \u5728\u65B0\u6807\u7B7E\u4E2D\u6253\u5F00\u6587\u4EF6 </li><li><kbd>Ctrl</kbd> + <kbd>wT</kbd> - \u5C06\u7A97\u53E3\u53D8\u6210\u6807\u7B7E </li><li><kbd>gt</kbd> or <kbd>:tabn[ext]</kbd> - \u5207\u6362\u5230\u4E0B\u4E00\u4E2A\u6807\u7B7E </li><li><kbd>gT</kbd> or <kbd>:tabp[revious]</kbd> - \u5207\u6362\u5230\u4E0A\u4E00\u4E2A\u6807\u7B7E </li><li><kbd>#gt</kbd> - \u5207\u6362\u5230\u7B2C &lt;kbd&gt;#&lt;/kbd&gt; \u4E2A\u6807\u7B7E </li><li><kbd>:tabm[ove] #</kbd> - \u79FB\u52A8\u6807\u7B7E\u5230\u7B2C &lt;kbd&gt;#&lt;/kbd&gt; \u4F4D(\u4E0B\u6807\u4ECE 0 \u5F00\u59CB) </li><li><kbd>:tabc[lose]</kbd> - \u5173\u95ED\u5F53\u524D\u6807\u7B7E </li><li><kbd>:tabo[nly]</kbd> - \u5173\u95ED\u5176\u4ED6\u6807\u7B7E </li><li><kbd>:tabdo</kbd> command - \u5728\u6240\u6709\u6807\u7B7E\u4E2D\u6267\u884C\u547D\u4EE4 (\u4F8B\u5982 <kbd>:tabdo q</kbd> \u5173\u95ED\u6240\u6709\u6807\u7B7E) </li></ul></div></div><div class="grid-block"><div class="grid-lg-1-2"><h2>\u591A\u6587\u4EF6\u7F16\u8F91</h2><ul><li><kbd>:e[dit] \u6587\u4EF6\u540D</kbd> - \u65B0\u5EFA\u7F13\u51B2\u533A\u6253\u5F00 filename </li><li><kbd>:bn[ext]</kbd> - \u5207\u6362\u5230\u4E0B\u4E2A\u7F13\u51B2\u533A </li><li><kbd>:bp[revious]</kbd> - \u5207\u6362\u5230\u4E0A\u4E2A\u7F13\u51B2\u533A </li><li><kbd>:bd[elete]</kbd> - \u5173\u95ED\u7F13\u51B2\u533A </li><li><kbd>:b[uffer]#</kbd> - go to a buffer by # </li><li><kbd>:b[uffer] file</kbd> - go to a buffer by file </li><li><kbd>:ls</kbd> or <kbd>:buffers</kbd> - \u5217\u51FA\u6240\u6709\u6253\u5F00\u7684\u7F13\u51B2\u533A </li><li><kbd>:sp[lit] \u6587\u4EF6\u540D</kbd> - \u65B0\u5EFA\u7F13\u51B2\u533A\u6253\u5F00 filename \u5E76\u6C34\u5E73\u5206\u5272\u7A97\u53E3 </li><li><kbd>:vs[plit] \u6587\u4EF6\u540D</kbd> - \u65B0\u7F13\u51B2\u533A\u6253\u5F00 filename \u5E76\u5782\u76F4\u5206\u5272\u7A97\u53E3 </li><li><kbd>:vert[ical] ba[ll]</kbd> - edit all buffers as vertical windows </li><li><kbd>:tab ba[ll]</kbd> - edit all buffers as tabs </li><li><kbd>Ctrl</kbd> + <kbd>ws</kbd> - \u6C34\u5E73\u5206\u5272\u7A97\u53E3 </li><li><kbd>Ctrl</kbd> + <kbd>wv</kbd> - \u5782\u76F4\u5206\u5272\u7A97\u53E3 </li><li><kbd>Ctrl</kbd> + <kbd>ww</kbd> - \u5728\u7A97\u53E3\u95F4\u5207\u6362 </li><li><kbd>Ctrl</kbd> + <kbd>wq</kbd> - \u5173\u95ED\u7A97\u53E3 </li><li><kbd>Ctrl</kbd> + <kbd>wx</kbd> - exchange current window with next one </li><li><kbd>Ctrl</kbd> + <kbd>w=</kbd> - make all windows equal height &amp; width </li><li><kbd>Ctrl</kbd> + <kbd>wh</kbd> - \u5207\u6362\u5230\u5DE6\u4FA7\u7A97\u53E3 </li><li><kbd>Ctrl</kbd> + <kbd>wl</kbd> - \u5207\u6362\u5230\u53F3\u4FA7\u7A97\u53E3 </li><li><kbd>Ctrl</kbd> + <kbd>wj</kbd> - \u5207\u6362\u5230\u4E0B\u4FA7\u7A97\u53E3 </li><li><kbd>Ctrl</kbd> + <kbd>wk</kbd> - \u5207\u6362\u5230\u4E0A\u4FA7\u7A97\u53E3 </li><li><kbd>Ctrl</kbd> + <kbd>wH</kbd> - \u4F7F\u6E38\u6807\u6240\u5728\u89C6\u7A97\u5168\u9AD8\u5E76\u79FB\u81F3\u6700\u5DE6 (\u6700\u5DE6\u5782\u76F4\u89C6\u7A97) </li><li><kbd>Ctrl</kbd> + <kbd>wL</kbd> - \u4F7F\u6E38\u6807\u6240\u5728\u89C6\u7A97\u5168\u9AD8\u5E76\u79FB\u81F3\u6700\u53F3 (\u6700\u53F3\u5782\u76F4\u89C6\u7A97) </li><li><kbd>Ctrl</kbd> + <kbd>wJ</kbd> - \u4F7F\u6E38\u6807\u6240\u5728\u89C6\u7A97\u5168\u5BBD\u5E76\u79FB\u81F3\u6700\u4E0B (\u6700\u4E0B\u6C34\u5E73\u89C6\u7A97) </li><li><kbd>Ctrl</kbd> + <kbd>wK</kbd> - \u4F7F\u6E38\u6807\u6240\u5728\u89C6\u7A97\u5168\u5BBD\u5E76\u79FB\u81F3\u6700\u4E0A (\u6700\u4E0A\u6C34\u5E73\u89C6\u7A97) </li></ul></div></div></div></div></main><h2>Diff</h2><ul><li><p><kbd>zf</kbd> - manually define a fold up to motion</p></li><li><p><kbd>zd</kbd> - delete fold under the cursor</p></li><li><p><kbd>za</kbd> - toggle fold under the cursor</p></li><li><p><kbd>zo</kbd> - open fold under the cursor</p></li><li><p><kbd>zc</kbd> - close fold under the cursor</p></li><li><p><kbd>zr</kbd> - reduce (open) all folds by one level</p></li><li><p><kbd>zm</kbd> - fold more (close) all folds by one level</p></li><li><p><kbd>zi</kbd> - toggle folding functionality</p></li><li><p><kbd>]c</kbd> - jump to start of next change</p></li><li><p><kbd>[c</kbd> - jump to start of previous change</p></li><li><p><kbd>do</kbd> or </p><p><kbd>:diffg[et]</kbd> - obtain (get) difference (from other buffer)</p></li><li><p><kbd>dp</kbd> or </p><p><kbd>:diffpu[t]</kbd> - put difference (to other buffer)</p></li><li><p><kbd>:diffthis</kbd> - make current window part of diff</p></li><li><p><kbd>:dif[fupdate]</kbd> - update differences</p></li><li><p><kbd>:diffo[ff]</kbd> - switch off diff mode for current window</p></li></ul><p><strong>Tip</strong> The commands for folding (e.g. </p><p><kbd>za</kbd>) operate on one level. To operate on all levels, use uppercase letters (e.g. </p><p><kbd>zA</kbd>).</p><p><strong>Tip</strong> To view the differences of files, one can directly start Vim in diff mode by running </p><p><kbd>vimdiff</kbd> in a terminal. One can even set this as </p><p><kbd>git difftool</kbd>.</p>',11),e=[t];function o(r,a,s,n,p,g){return b(),l("div",null,e)}var f=d(k,[["render",o]]);export{c as __pageData,f as default};
